(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let m;const s=e=>e<10?"0"+e:e;m=setInterval((()=>{let e=(()=>{let e=(new Date("30 december 2022").getTime()-(new Date).getTime())/1e3;return{hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60),timeRemaining:e}})();t.textContent=s(e.hours),n.textContent=s(e.minutes),o.textContent=s(e.seconds),e.timeRemaining>0||(clearInterval(m),t.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})()})();